declare module "*.module.less" {
	const resource: { [key: string]: string };
	export default resource;
}

declare module "*.lua" {
	const value: string;
	export default value;
}

declare module "*.ogg" {
	const value: string;
	export default value;
}

declare module "*.png" {
	const value: string;
	export default value;
}

declare module "*.template" {
	const value: string;
	export default value;
}
